contractbuy = web3.eth.contract(address=pancakeswap_router, abi=pancakeswap_router_abi)

wbnb = web3.toChecksumAddress("0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c")
spender_address = web3.toChecksumAddress("0x5406b496E19E24F3c3706dD7B3eF0C315ab66Aa0")
token_to_buy = web3.toChecksumAddress("0xB27ADAfFB9fEa1801459a1a81B17218288c097cc")



def buy():
    time.sleep(20)
    spend = web3.toChecksumAddress("0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c")
    nonce = web3.eth.get_transaction_count(spender_address)
    pancakeswap2_tx = contractbuy.functions.swapExactETHForTokens(
        0,
        [spend, token_to_buy],
        spender_address,
        (int(time.time()) + 10000)
    ).buildTransaction({
        'from': spender_address,
        'value': web3.toWei(0.0001, 'ether'),
        'gasPrice': web3.toWei('5', 'gwei'),
        'nonce': nonce
    })

    signed_tx = web3.eth.account_sign_transaction(pancakeswap2_tx, private_key=config.private)
    tx_token= web3.eth.send_raw_transaction(signed_tx.rawTransaction)
    print('Snipe was successful, bought: ', web3.toHex(tx_token))
  

def handle_event(event):
    pair = Web3.toJSON(event)
    print(pair)

    token0 = str(Web3.toJSON(event['args']['token1']))
    token1 = str(Web3.toJSON(event['args']['token0']))
    block = Web3.toJSON(event['blockNumber'])
    txhash = Web3.toJSON(event['transactionHash'])

    #print
    print('block: ', block)
    print('Transaction Hash: ', txhash)
    print('Token0: ', token0)
    print('Token1: ', token1)

    wbnb2 = wbnb.upper()
    token_to_buy_2 = token_to_buy.upper()

    if(token0.upper().strip('*') == wbnb2 and token1.upper().strip('*') == token_to_buy_2):
        print('pair detected')
        buy()
    elif(token0.upper().strip('*') == token_to_buy_2 and token1.upper().strip('*') == wbnb2):
        print('pair detected')
        buy()
    else:
        print('next pair')



async def log_loop(event_filter, poll_interval):
    while True:
        for PairCreated in event_filter.get_new_entries():
            handle_event(PairCreated)
        await asyncio.sleep(poll_interval)

def main():
    event_filter = contract.events.PairCreated.createFilter(fromBlock='latest')
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
            log_loop(event_filter, 2)
            ))
    finally:
        loop.close()


