# approve token
sell_amount_ether = web3.from_wei(sell_amount_wei, 'ether')
start = time.time()

nonce = web3.eth.get_transaction_count(sender)
amount = web3.to_wei(sell_bal, 'ether')

tx = {
    'nonce': nonce,
    'gas': 100000,
    'gasPrice': web3.to_wei('5', 'gwei'),
    'chainId': 56
}
