amount_in = w3.to_wei(0.001, 'ether')
min_amount_out = 1  # 365 * 10**18


path = [weth_address, pepe_address]


codec = RouterCodec()

data, signable_message = codec.create_permit2_signable_message(
    uni_address,
    2**160 - 1,  # max = 2**160 - 1
    RouterCodec.get_default_expiration(),
    w3.eth.get_transaction_count(account),  # Permit2 nonce
    pepe_address,  # UR
    RouterCodec.get_default_deadline(),
    1,  # chain id
)
signed_message = w3.eth.account.sign_message(signable_message, private_key)

encoded_input = (
    codec
    .encode
    .chain()
    .permit2_permit(data, signed_message)
    .wrap_eth(FunctionRecipient.ROUTER, amount_in)
    .v2_swap_exact_in(FunctionRecipient.SENDER, amount_in, min_amount_out, path, payer_is_sender=False)
    .build(codec.get_default_deadline())
)

print('encoded_input: ', encoded_input)
print("nonce: ", w3.eth.get_transaction_count(account))
trx_params = {
    "from": account,
    "to": uni_address,
    "gas": 100000,
    "maxPriorityFeePerGas": w3.eth.max_priority_fee,
    "maxFeePerGas": 10 * 10**9,
    "chainId": chain_id,
    "value": amount_in,
    "nonce": 4,
    "data": encoded_input,
}

rawtransaction = w3.eth.account.sign_transaction(trx_params, private_key)
send_txn = w3.eth.send_raw_transaction(rawtransaction.rawTransaction)
print(w3.to_hex(send_txn))

